mapper.insert:
INSERT INTO
    service_url
VALUES (
    url_id = #{urlId}
    service_id = #{serviceId}
    url_content = #{urlContent}
    url_type = #{urlContent}
    param = #{content}
    body = #{body}
    description = #{description}
    update_time = #{updateTime}
)

mapper.update:
UPDATE
    service_url
SET
    service_id = #{serviceId}
    url_content = #{urlContent}
    url_type = #{urlContent}
    param = #{content}
    body = #{body}
    description = #{description}
    update_time = #{updateTime}
WHERE
    url_id = #{urlId}
